import React, { useContext, useState } from "react";
import { GlobalContext } from "../context/Provider";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";

const OptionsPage = () => {
  const globalStore: any = useContext(GlobalContext);
  const { optionId } = useParams();

  const textGenerator = (textArray: Array<String>) => {
    return textArray.map((i) => <span className="text-spn"> {i} </span>);
  };

  const buttonGenerator = (
    btnObject:
      | Array<{
          status: string | undefined;
          text: string;
        }>
      | undefined
  ) => {
    return (
      <div className="horizontal-btn">
        {btnObject?.map((i: { text: string }) => (
          <button key={i.text} className="btn-style btn-space">
            <span>{i.text}</span>
          </button>
        ))}
        d{console.log("Check check", btnObject)}
      </div>
    );
  };

  return (
    <div>
      {console.log(
        "Checl",
        globalStore,
        optionId,
        parseInt(optionId!) - 1,
        globalStore.webData[globalStore.dataIndex].payload.excuses[
          parseInt(optionId!) - 1
        ].explanations
      )}
      {/* {
        globalStore.webData[globalStore.dataIndex].excuses[optionId!]
          .explanations[0]
      } */}
      You chose :{" "}
      {
        globalStore.webData[globalStore.dataIndex].payload.excuses[
          parseInt(optionId!) - 1
        ].text
      }
      {textGenerator(
        globalStore.webData[globalStore.dataIndex].payload.excuses[
          parseInt(optionId!) - 1
        ].explanations[0].texts
      )}
      {buttonGenerator(
        globalStore.webData[globalStore.dataIndex].payload.excuses[
          parseInt(optionId!) - 1
        ].explanations[0].possibleReplies
      )}
    </div>
  );
};

export default OptionsPage;
