import React, { useContext } from "react";
import "./SubPage.css";
import "../App.css";
import { GlobalContext } from "../context/Provider";
import { Link } from "react-router-dom";

interface SubPageProps {
  content: { payload: { texts: Array<String> | undefined } } | undefined;
}

const SubPage = ({ content }: SubPageProps) => {
  const globalStore: any = useContext(GlobalContext);

  const textGenerator = (textArray: Array<String>) => {
    return textArray.map((i) => <span className="text-spn"> {i} </span>);
  };

  const buttonGenerator = (
    btnObject:
      | {
          style: { buttonLayout: string } | undefined;
          list: Array<{
            text: string;
            nextPageId: number;
            variable: string;
            value: string;
          }>;
        }
      | undefined
  ) => {
    if (btnObject?.style?.buttonLayout === " horizontal") {
      <div className="horizontal-btn">
        {btnObject.list.map((i) => (
          <button>{i.text}</button>
        ))}
      </div>;
    }
  };

  return (
    <div className="sub-page">
      {textGenerator(content?.payload.texts!)}
      <div>
        <Link to={`/goVegan/pages/${globalStore.contentId - 1}`}>
          <button
            className="btn-style"
            onClick={() => globalStore.setContentId(globalStore.contentId - 1)}
          >
            <span> Back</span>
          </button>
        </Link>
        <Link to={`/goVegan/pages/${globalStore.contentId + 1}`}>
          <button
            className="btn-style"
            onClick={() => globalStore.setContentId(globalStore.contentId + 1)}
          >
            <span> Continue</span>
          </button>
        </Link>
      </div>
    </div>
  );
};

export default SubPage;
