import React, { useContext, useState } from "react";
import { GlobalContext } from "../context/Provider";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";

const OptionsPage = () => {
  const globalStore: any = useContext(GlobalContext);
  const { optionId } = useParams();

  const textGenerator = (textArray: Array<String>) => {
    return textArray.map((i) => <span className="text-spn"> {i} </span>);
  };

  const dataLog =
    globalStore.webData[globalStore.dataIndex].payload.excuses[
      parseInt(optionId!) - 1
    ];

  const buttonGenerator = (
    btnObject:
      | Array<{
          status: string | undefined;
          text: string;
        }>
      | undefined
  ) => {
    return (
      <div className="horizontal-btn">
        {btnObject?.map((i: { text: string }) => (
          <button
            key={i.text}
            className="btn-style btn-space"
            onClick={() => globalStore.shiftOptionsSelected()}
          >
            <span>{i.text}</span>
          </button>
        ))}
      </div>
    );
  };

  return (
    <div>
      {console.log(
        "Checl",
        globalStore,
        optionId,
        parseInt(optionId!) - 1,
        dataLog.explanations
      )}
      {/* {
        globalStore.webData[globalStore.dataIndex].excuses[optionId!]
          .explanations[0]
      } */}
      You chose : {dataLog.text}
      {textGenerator(dataLog.explanations[0].texts)}
      {buttonGenerator(dataLog.explanations[0].possibleReplies)}
    </div>
  );
};

export default OptionsPage;
