import React, { useContext } from "react";
import "./SubPage.css";
import "../App.css";
import { GlobalContext } from "../context/Provider";
import { Link } from "react-router-dom";
import _ from "lodash";

interface SubPageProps {
  content: { payload: { texts: Array<String> | undefined } } | undefined;
}

const SubPage = ({ content }: SubPageProps) => {
  const globalStore: any = useContext(GlobalContext);

  const Index = _.findIndex(globalStore.webData, {
    pageId: globalStore.contentId,
  });

  const textGenerator = (textArray: Array<String>) => {
    return textArray.map((i) => <span className="text-spn"> {i} </span>);
  };

  const buttonGenerator = (
    btnObject:
      | {
          style: { buttonLayout: string } | undefined;
          list: Array<{
            text: string;
            nextPageId: number;
            variable: string;
            value: string;
          }>;
        }
      | undefined
  ) => {
    if (btnObject?.style?.buttonLayout === " vertical") {
      return (
        <div className="vertical-btn btn-space">
          {btnObject.list.map((i: { text: string; nextPageId: number }) => (
            <Link to={{ pathname: `/goVegan/pages/${i.nextPageId}` }}>
              <button
                key={i.text}
                className="btn-style btn-space"
                onClick={() => globalStore.setContentId(i.nextPageId)}
              >
                <span>{i.text}</span>
              </button>
            </Link>
          ))}
        </div>
      );
    } else {
      return (
        <div className="horizontal-btn">
          {btnObject?.list.map((i: { text: string; nextPageId: number }) => (
            <Link to={{ pathname: `/goVegan/pages/${i.nextPageId}` }}>
              <button
                key={i.text}
                className="btn-style btn-space"
                onClick={() => globalStore.setContentId(i.nextPageId)}
              >
                <span>{i.text}</span>
              </button>
            </Link>
          ))}
        </div>
      );
    }
  };

  return (
    <div className="sub-page">
      {textGenerator(content?.payload.texts!)}
      {buttonGenerator(globalStore.webData[Index].payload.buttons)}
      {/* <div>
        <Link to={`/goVegan/pages/${globalStore.contentId - 1}`}>
          <button
            className="btn-style"
            onClick={() => globalStore.setContentId(globalStore.contentId - 1)}
          >
            <span> Back</span>
          </button>
        </Link>
        <Link to={`/goVegan/pages/${globalStore.contentId + 1}`}>
          <button
            className="btn-style"
            onClick={() => globalStore.setContentId(globalStore.contentId + 1)}
          >
            <span> Continue</span>
          </button>
        </Link>
      </div> */}
    </div>
  );
};

export default SubPage;
